const versions = {
  0: ({ topics, timeout }) => {
    const request = require('kafkajs/src/protocol/requests/createTopics/v0/request')
    const response = require('kafkajs/src/protocol/requests/createTopics/v0/response')
    return { request: request({ topics, timeout }), response }
  },
  1: ({ topics, validateOnly, timeout }) => {
    const request = require('kafkajs/src/protocol/requests/createTopics/v1/request')
    const response = require('kafkajs/src/protocol/requests/createTopics/v1/response')
    return { request: request({ topics, validateOnly, timeout }), response }
  },
  2: ({ topics, validateOnly, timeout }) => {
    const request = require('kafkajs/src/protocol/requests/createTopics/v2/request')
    const response = require('kafkajs/src/protocol/requests/createTopics/v2/response')
    return { request: request({ topics, validateOnly, timeout }), response }
  },
}

module.exports = {
  versions: Object.keys(versions),
  protocol: ({ version }) => versions[version],
}
