const versions = {
  0: ({ topics, timeout }) => {
    const request = require('kafkajs/src/protocol/requests/deleteTopics/v0/request')
    const response = require('kafkajs/src/protocol/requests/deleteTopics/v0/response')
    return { request: request({ topics, timeout }), response }
  },
  1: ({ topics, timeout }) => {
    const request = require('kafkajs/src/protocol/requests/deleteTopics/v1/request')
    const response = require('kafkajs/src/protocol/requests/deleteTopics/v1/response')
    return { request: request({ topics, timeout }), response }
  },
}

module.exports = {
  versions: Object.keys(versions),
  protocol: ({ version }) => versions[version],
}
