const versions = {
  0: ({ resources }) => {
    const request = require('kafkajs/src/protocol/requests/describeConfigs/v0/request')
    const response = require('kafkajs/src/protocol/requests/describeConfigs/v0/response')
    return { request: request({ resources }), response }
  },
  1: ({ resources, includeSynonyms }) => {
    const request = require('kafkajs/src/protocol/requests/describeConfigs/v1/request')
    const response = require('kafkajs/src/protocol/requests/describeConfigs/v1/response')
    return { request: request({ resources, includeSynonyms }), response }
  },
}

module.exports = {
  versions: Object.keys(versions),
  protocol: ({ version }) => versions[version],
}
