const versions = {
  0: ({ groupId, groupGenerationId, memberId }) => {
    const request = require('kafkajs/src/protocol/requests/heartbeat/v0/request')
    const response = require('kafkajs/src/protocol/requests/heartbeat/v0/response')
    return {
      request: request({ groupId, groupGenerationId, memberId }),
      response,
    }
  },
  1: ({ groupId, groupGenerationId, memberId }) => {
    const request = require('kafkajs/src/protocol/requests/heartbeat/v1/request')
    const response = require('kafkajs/src/protocol/requests/heartbeat/v1/response')
    return {
      request: request({ groupId, groupGenerationId, memberId }),
      response,
    }
  },
}

module.exports = {
  versions: Object.keys(versions),
  protocol: ({ version }) => versions[version],
}
